mapper.select:
select c.id, c.content_id, c.content_title, c.content_lang, c."content", c.is_draft, c.save_dat, c.user_id, c.approve_dat, c.approve_user_id, c.mod_dat, c.cre_dat from user_data."content" c, user_data.content_registry cr where c.user_id = #{userId} and cr.content_id = c.content_id
and c.content_id like #{contentIdPrefix} || '%'
and c.save_dat = ( select max(c2.save_dat) from user_data."content" c2 where c.content_id = c2.content_id ) order by c.save_dat desc limit #{limit}

with help as ( select cr.anon_allow_view as allow_view, cr.anon_allow_edit as allow_edit from user_data.content_registry cr where cr.content_id = #{contentId}
union all select cr.user_allow_view as allow_view, cr.user_allow_edit as allow_edit from user_data.content_registry cr where cr.content_id = #{contentId}
union all select car.allow_view, car.allow_edit from user_data.content_access_role car, access_data."role" r where car.content_id = #{contentId} and car.role_id = r.id and r."name" in
#{ga.authority}
) select bool_or(allow_view) from help h

with help as ( select cr.anon_allow_view as allow_view, cr.anon_allow_edit as allow_edit from user_data.content_registry cr where cr.content_id = #{contentId}
union all select cr.user_allow_view as allow_view, cr.user_allow_edit as allow_edit from user_data.content_registry cr where cr.content_id = #{contentId}
union all select car.allow_view, car.allow_edit from user_data.content_access_role car, access_data."role" r where car.content_id = #{contentId} and car.role_id = r.id and r."name" in
#{ga.authority}
) select bool_or(allow_edit) from help h

with help as (
select true as allow_approval from user_data.content_registry cr where cr.content_id = #{contentId} and anon_approval_by_role in
#{ga.authority}
union all select true as allow_approval from user_data.content_registry cr where cr.content_id = #{contentId} and cr.user_approval_by_role in
#{ga.authority}
union all
select ( select r2."name" in
#{ga.authority}
from user_data.content_access_role car2, access_data."role" r2 where car2.content_id = car.content_id and car2.approval_by_role = r2.id ) from user_data.content_access_role car, access_data."role" r where car.content_id = #{contentId} and car.role_id in
#{r.id}
) select bool_and(allow_approval) from help
